import React from "react";
import Login from "./components/Login";
import Nav from "./components/Nav";
import Main from "./components/Main";
import Workout from "./components/Workout";
import Activity from "./components/Activity";
import Admin from "./components/Admin";
import Toast from "./components/Toast";

class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			isLoggedIn: false,
			toast: {
				text: "",
				isActive: false,
				type: "info"
			}
		};
	}

	showToast = (message, isError) => {
		this.setState({
			toast: {
				text: message,
				isActive: true,
				type: isError ? "error" : "info"
			}
		});

		setTimeout(() => {
			this.setState({
				toast: {
					text: "",
					isActive: false,
					type: "info"
				}
			});
		}, 4000);
	}

	componentDidMount() {
		fetch("http://huntingtonbeach.beynum.com:9005/workout/api/appload", { credentials: "include" })
			.then(response => response.json())
			.then(data => {

				this.setState({
					isLoggedIn: data.loggedIn,
					page: data.loggedIn ? <Main toast={this.showToast} /> : <Login toast={this.showToast} />,
					isLoading: false
				});

			});
	}

	changePage = (page) => {
		if (this.state.isLoggedIn) {
			switch (page) {
				case "home":
					this.setState({ page: <Main toast={this.showToast} /> });
					break;

				case "workout":
					this.setState({ page: <Workout toast={this.showToast} /> });
					break;

				case "activity":
					this.setState({ page: <Activity toast={this.showToast} /> });
					break;

				case "admin":
					this.setState({ page: <Admin toast={this.showToast} /> });
					break;

				default:
					break;
			}
		}
	}

	render() {
		return (
			<div className="pageContainer">
			{
				this.state.isLoading ?
					<div>Loading</div>
				:
				this.state.page
			}
			{	
				this.state.isLoggedIn ? <Nav changePage={ this.changePage } /> : null
			}
				<Toast message={ this.state.toast } />
			</div>
		);
	}

}

export default App;
