import React from "react";
import "./activity.css"
import loadingImage from "../media/loading.gif";

class Activity extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			workout: { id: props.workoutId }
		};
	}

	getWeights = (totalWeight, exercise) => {
		let weights = {};

		totalWeight -= exercise.hasBar ? 45 : 0;

		weights.has45 = totalWeight >= 45 * 2;
		totalWeight -= totalWeight >= 45 * 2 ? 45 * 2 : 0;

		weights.has25 = totalWeight >= 25 * 2;
		totalWeight -= totalWeight >= 25 * 2 ? 25 * 2 : 0;

		weights.has10 = totalWeight >= 10 * 2;
		totalWeight -= totalWeight >= 10 * 2 ? 10 * 2 : 0;

		weights.has5 = totalWeight >= 5 * 2;
		totalWeight -= totalWeight >= 5 * 2 ? 5 * 2 : 0;

		weights["has2.5"] = totalWeight >= 2.5 * 2;
		totalWeight -= totalWeight >= 2.5 * 2 ? 2.5 * 2 : 0;

		return weights;
	}

	componentDidMount() {
		fetch("http://huntingtonbeach.beynum.com:9005/workout/api/activityload?id=" + this.state.workout.id, { credentials: "include" })
			.then(response => response.json())
			.then(data => {
				let workout = data.workout;
				workout.sets = data.workout.sets.map(setItem => ({
					...setItem,
					...this.getWeights(setItem.goalWeight, setItem.exercise)
				}));

				this.setState({
					workout: workout,
					setsComplete: 0,
					startTime: new Date(),
					isLoading: false
				});
			})
			.catch(error => {
				console.warn(error);
				this.props.toast("Error loading workout", true);
			});
	}

	render() {
		return (
			<div className="page activityPage">
			{
			this.state.isLoading ?
				<div className="loading">
					<img alt="Loading" src={loadingImage} />
					<div>Loading...</div>
				</div>
			:
				<div>
					<div className="activityCard">
						<div>{this.state.workout.name}</div>
						<div className="cardDetails">
							<div className="cardInfo">
								<div className="cardLabel">Time</div>
								<div className="cardData">{ this.state.minElapsed } min { this.state.secElapsed } sec</div>
							</div>

							<div className="cardInfo">
								<div className="cardLabel">Sets remain </div>
								<div className="cardData">{ this.state.workout.sets.length - this.state.setsComplete }</div>
							</div>
						</div>
						<div className="cardRemaining">{this.state.setsComplete > 0 ? Math.round((this.state.setsComplete / this.state.workout.sets.length) * 100) : 0 }</div>
					</div>
				</div>
			}
			</div>
		);
	}
}

export default Activity;
