import React from "react";
import "./admin.css";

class Admin extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isUserExpanded: false,
			isRoleExpanded: false,
			users: [],
			roles: []
		};
	}

	componentDidMount() {
		this.setState({
			users: [
				{ id: 1, name: "Brett van Beynum", roles: [{ id: 1, name: "Admin" }] }, 
				{ id: 2, name: "Luke van Beynum", roles: [] }
			]
		});

		this.setState({
			roles: [{ id: 1, name: "Admin" }]
		});
	}

	getUserStyle = (expandable) => {
		return {
			display: expandable ? "flex": "none"
		};
	}

	setExpanded = (expandableType) => {
		if (expandableType == "user") {
			this.setState({ isUserExpanded: true, isRoleExpanded: false });
		}
		else {
			this.setState({ isUserExpanded: false, isRoleExpanded: true });
		}
	}

	render() {
		return (
			<div className="page adminPage">
				<div className="headerBackground">
				</div>

				<div className="container" onClick={ this.setExpanded("user") }>
					<div className="subHeader">{ this.state.users.length } users total</div>
					<div className="header">Users</div>

					<div className="listContainer" style={this.getUserStyle(this.state.isUserExpanded)}>

					</div>
				</div>

				<div className="container" onClick={ this.setExpanded("role") }>
					<div className="subHeader">{ this.state.roles.length } roles total</div>
					<div className="header">Roles</div>

					<div className="listContainer" style={this.getUserStyle(this.state.isRoleExpanded)}>

					</div>
				</div>
			</div>
		);
	}
}

export default Admin;
