import React from "react";
import "./workout.css";

// Design: https://dribbble.com/shots/5715392-Adasse-Beta-2-Mobile-App-Design/attachments/1446974?mode=media
// Card palette: https://i.pinimg.com/originals/33/e1/da/33e1da639d67589891ddbfef0461cb04.png

class Workout extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			workouts: [
				{ 
					id: 1, name: "Workout 1", lastWorkout: new Date(2020, 10, 10), sets: [
						{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, hasWeight: false, rest: 180 }, 
						{ exerciseId: 2, exerciseName: "Bench Press", goalReps: 10, hasWeight: true, goalWeight: 135, rest: 145 }, 
						{ exerciseId: 3, exerciseName: "Row", goalReps: 10, hasWeight: true, goalWeight: 145, rest: 145 }
						] 
				},
				{ 
					id: 2, name: "Workout 2", lastWorkout: new Date(2020, 10, 5), isExpanded: false, sets: [
						{ exerciseId: 1, exerciseName: "Crunch", goalReps: 30, hasWeight: true, goalWeight: 25, rest: 180 }, 
						{ exerciseId: 2, exerciseName: "Squat", goalReps: 7, hasWeight: true, goalWeight: 135, rest: 145 }, 
						{ exerciseId: 3, exerciseName: "Pull-up", goalReps: 10, hasWeight: false, rest: 145 }
						] 
				}
				],
			exercises: [
				{ id: 1, name: "Hanging Knee Raise", category: "Abs", hasWeight: false, hasBar: false },
				{ id: 2, name: "Bench Press", category: "Chest", hasWeight: true, hasBar: true },
				{ id: 3, name: "Squat", category: "Legs", hasWeight: true, hasBar: true },
				{ id: 4, name: "Row", category: "Back", hasWeight: true, hasBar: false },
				{ id: 5, name: "Crunch (weight)", category: "Abs", hasWeight: true, hasBar: false }
				]
		};
	}

	expandWorkout = workoutIndex => event => {
		let workouts = [...this.state.workouts];

		if (event.target.tagName.toLowerCase() === "path" || event.target.tagName.toLowerCase() === "svg") {
			workouts.forEach((workout, index) => { workout.isEdit = workoutIndex === index ? !workout.isEdit : false });
		}
		else if (event.target.tagName.toLowerCase() === "div") {
			workouts.forEach((workout, index) => { workout.isExpanded = workoutIndex === index ? !workout.isExpanded : false });
		}

		this.setState({workouts});
	}

	editRest = (workoutIndex, setIndex) => event => {
		let newRest = 0,
			updatedRest = event.target.value ? +event.target.value : 0;

		if (event.target.name === "min") {
			newRest = (updatedRest * 60) + this.state.workouts[workoutIndex].sets[setIndex].rest % 60;
		}
		else {
			newRest = (Math.floor(this.state.workouts[workoutIndex].sets[setIndex].rest / 60) * 60) + updatedRest;
		}

		this.setState(({workouts}) => ({
			workouts: [
				...workouts.slice(0, workoutIndex),
				{
					...workouts[workoutIndex],
					sets: [
						...workouts[workoutIndex].sets.slice(0, setIndex),
						{
							...workouts[workoutIndex].sets[setIndex],
							rest: newRest
						},
						...workouts[workoutIndex].sets.slice(setIndex + 1)
						]
				},
				...workouts.slice(workoutIndex + 1)
				]
		}));
	}

	editSet = (workoutIndex, setIndex) => event => {
		console.log(event.target.name);
		console.log(event.target.value);
		this.setState(({workouts}) => ({
			workouts: [
				...workouts.slice(0, workoutIndex),
				{
					...workouts[workoutIndex],
					sets: [
						...workouts[workoutIndex].sets.slice(0, setIndex),
						{
							...workouts[workoutIndex].sets[setIndex],
							[event.target.name]: Number.isInteger ? +event.target.value : event.target.value
						},
						...workouts[workoutIndex].sets.slice(setIndex + 1)
						]
				},
				...workouts.slice(workoutIndex + 1)
				]
		}));

		// let workouts = [...this.state.workouts];
		// let workoutEdit = {
		// 	...workouts[workoutIndex],
		// 	sets: [
		// 		...workouts[workoutIndex].sets.slice(0, setIndex),
		// 		{
		// 			...workouts[workoutIndex].sets[setIndex],
		// 			goalReps: 1
		// 		},
		// 		...workouts[workoutIndex].sets.slice(setIndex + 1)
		// 		]
		// };
		// workouts[workoutIndex] = workoutEdit;
		// this.setState({workouts});
	}

	addSet = workoutIndex => event => {
		this.setState(({workouts}) => ({
			workouts: [
				...workouts.slice(0, workoutIndex),
				{
					...workouts[workoutIndex],
					sets: [
						...workouts[workoutIndex].sets,
						{
							id: Math.max(...workouts[workoutIndex].sets.map(setItem => setItem.id)) + 1,
							exerciseName: "",
							goalReps: 0,
							goalWeight: 0,
							rest: 0
						}
						]
				},
				...workouts.slice(workoutIndex + 1)
				]
		}));
	}

	addWorkout = event => {
		this.setState(({workouts}) => ({
			workouts: [...workouts, { id: Math.max(...workouts.map(workout => workout.id)) + 1, name: "New Workout", sets: []}]
		}));
	}

	render() {
		return (
			<div className="page workoutPage">
				<div className="headerBackground">
					Workouts
				</div>

				{
				this.state.workouts.map((workout, workoutIndex) => (

				<div key={workout.id} className={`card ${ workout.isExpanded ? "cardExpanded" : "" }`}>
					<div className="cardContent" onClick={ this.expandWorkout(workoutIndex) }>

						{ workout.isEdit ?
						<div className="cardHeader">
							<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
								<path d="M17 3H5c-1.11 0-2 .9-2 2v14c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V7l-4-4zm-5 16c-1.66 0-3-1.34-3-3s1.34-3 3-3 3 1.34 3 3-1.34 3-3 3zm3-10H5V5h10v4z"/>
							</svg>
							<input type="text" value={ workout.name } onChange={e => { }} />
						</div>
						:
						<div className="cardHeader">
							<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
								<path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z"/>
							</svg>
							{ workout.name }
						</div>
						}

						<div className="cardSubHeader">
							{ workout.lastWorkout ? "Last " + workout.lastWorkout.toDateString() : "Not Started" }&nbsp;&bull;&nbsp;
							{ workout.sets.reduce((total, current) => total + current.goalReps, 0) + " total reps" }
						</div>

					</div>

					<div className={`cardSelector ${ workout.isExpanded ? "selectorActive" : "" }`}>
						{
						workout.sets.map((setItem, setIndex) => (
							<div key={setIndex} className="subCard">
								<div className="setNumber">Set { setIndex + 1 }</div>
								<div className="exercise">
									<select name="exerciseName" value={ setItem.exerciseName } onChange={this.editSet(workoutIndex, setIndex)}>
										<option value=""></option>
										{
										this.state.exercises.map((exercise, exerciseIndex) => (
											<option key={exerciseIndex} value={exercise.name}>{exercise.name}</option>
										))
										}
									</select>
								</div>

								<div className="horizontalContainer repWeight">
									<div className="inputLabel">
										<input type="number" name="goalReps" value={setItem.goalReps} onChange={this.editSet(workoutIndex, setIndex)} />
										Reps
									</div>
									{ setItem.hasWeight && <div>x</div> }
									{ setItem.hasWeight &&
									<div className="inputLabel">
										<input type="number" name="goalWeight" value={setItem.goalWeight} onChange={this.editSet(workoutIndex, setIndex)} />
										Weight
									</div>
									}
								</div>

								{ setItem.hasWeight && 
								<div className="horizontalContainer">
									<div className="data">{setItem.hasWeight ? setItem.goalReps + setItem.goalWeight : setItem.goalReps}</div>
									<div className="label">lbs</div>
								</div>
								}

								<div className="horizontalContainer">

									<div className="inputLabel">
										<input type="number" name="min" value={Math.floor(setItem.rest / 60)} onChange={this.editRest(workoutIndex, setIndex)} />
										min
									</div>
									:
									<div className="inputLabel">
										<input type="number" name="sec" value={setItem.rest % 60} onChange={this.editRest(workoutIndex, setIndex)} />
										sec
									</div>

									<div className="inputLabel">rest</div>
								</div>
							</div>
						))
						}


						<div className="subCard">
							<div className="subCardNew" onClick={this.addSet(workoutIndex)}>
								<div>+</div>
							</div>
						</div>
					</div>
				</div>

				))
				}

				<div className="floatingButton" onClick={this.addWorkout}>+</div>
			</div>
		);
	}
}

export default Workout;
