// eslint-disable-next-line
/* global fetch */

import React from "react";
import "./workoutactivity.css";
import loadingImage from "../media/loading.gif";

class WorkoutActivity extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			workout: { id: this.props.workoutId }
		};
	}

	componentDidMount() {

		fetch("http://workout.beynum.com/workout/api/activityload?id=" + this.state.workout.id, { credentials: "include" })
			.then(response => response.json())
			.then(data => {
				const workout = data.workout;

				workout.sets = workout.sets.map(workoutSet => {
					const graphDates = [...new Set(workoutSet.exercise.activities.map(activity => +(new Date(activity.runDateOnly))))];

					return {
						...workoutSet,
						...this.getWeights(workoutSet.exercise.maxWeight, workoutSet.exercise),
						graph: {
							days: graphDates.map((date, dateIndex) => ({
								date: ((new Date(date)).getMonth() + 1) + "/" + (new Date(date)).getDate(),
								oneRepMax: workoutSet.exercise.activities
									.filter(activity => +(new Date(activity.runDateOnly)) === date)
									.map(activity => activity.oneRepMax)
									.sort((activityA, activityB) => activityB - activityA)[0],
								setLoads: workoutSet.exercise.activities
									.filter(activity => +(new Date(activity.runDateOnly)) === date)
									.map(activity => activity.load)
							}))
						}
					};
				});

				workout.sets = workout.sets.map(workoutSet => {
					if (!workoutSet.exercise.hasWeight) {
						return workoutSet;
					}

					const graphSize = { width: 350, height: 200 },
						minValue = Math.min(...workoutSet.graph.days.map(date => date.oneRepMax)),
						maxValue = Math.max(...workoutSet.graph.days.map(date => date.oneRepMax)),
						minGraph = minValue - (minValue * .1),
						maxGraph = maxValue + (maxValue * .1),
						valueHeight = maxGraph - minGraph;

					let path = workoutSet.graph.days.map((day, dayIndex) => {
						const xWidth = graphSize.width / workoutSet.graph.days.length,
							pointX = ((graphSize.width / workoutSet.graph.days.length) * dayIndex),
							pointY = ((maxGraph - day.oneRepMax) * graphSize.height) / valueHeight;

						let path = "";

						if (dayIndex === 0) {
							path = "M0,";
						}
						else if (dayIndex === workoutSet.graph.days.length - 1) {
							path = "L" + graphSize.width + ",";
						}
						else {
							path = "L" + (pointX + (xWidth / 2)) + ",";
						}

						path += pointY;

						return path;

						}).join(" ");

					return {
						...workoutSet,
						graph: {
							...workoutSet.graph,
							path: path,
							pathFill: path + " L" + graphSize.width + "," + graphSize.height + " L0," + graphSize.height,
							minValue: minGraph,
							maxValue: maxGraph
						}
					};
				});

				this.setState({
					workout: workout,
					isLoading: false
				});
			});
	}

	getWeights = (totalWeight, exercise) => {
		let weights = {};

		totalWeight -= exercise.hasBar ? 45 : 0;

		weights.has45 = totalWeight >= 45 * 2;
		totalWeight -= totalWeight >= 45 * 2 ? 45 * 2 : 0;

		weights.has25 = totalWeight >= 25 * 2;
		totalWeight -= totalWeight >= 25 * 2 ? 25 * 2 : 0;

		weights.has10 = totalWeight >= 10 * 2;
		totalWeight -= totalWeight >= 10 * 2 ? 10 * 2 : 0;

		weights.has5 = totalWeight >= 5 * 2;
		totalWeight -= totalWeight >= 5 * 2 ? 5 * 2 : 0;

		weights["has2.5"] = totalWeight >= 2.5 * 2;
		totalWeight -= totalWeight >= 2.5 * 2 ? 2.5 * 2 : 0;

		return weights;
	}

	render() {
		return (
			<div className="page">
			{
			this.state.isLoading ?
				<div className="loading">
					<img alt="Loading" src={loadingImage} />
					<div>Loading...</div>
				</div>
			:
				<div className="pageContainer">
					<h1>Workout { this.state.workout.name }</h1>

					<div className="setsContainer">	
					{
					this.state.workout.sets.map((workoutSet, setIndex) => (
						<div key={setIndex} className="setContainer">

							<div className="setHeader">
								<div className="setNameContainer">
									<div className="header">{ workoutSet.exercise.name }</div>
									<div className="subHeader">{ workoutSet.exercise.category }</div>
								</div>

								<div className="setNumberContainer">
									<div>{ setIndex + 1 }</div>
									<div>{ this.state.workout.sets.length }</div>
								</div>
							</div>

							<div className="setDataContainer">
								<div className="horizontalContainer">
									<div className="dataButton">-</div>
									<div className="dataValue">{ workoutSet.exercise.maxReps }</div>
									<div className="dataButton">+</div>
								</div>
								{
								workoutSet.exercise.hasWeight ? 
								<div className="horizontalContainer">
									<div className={`weightButton ${ workoutSet["has2.5"] ? "active" : "" }`}>2.5</div>
									<div className={`weightButton ${ workoutSet["has5"] ? "active" : "" }`}>5</div>
									<div className={`weightButton ${ workoutSet["has10"] ? "active" : "" }`}>10</div>
									<div className={`weightButton ${ workoutSet["has25"] ? "active" : "" }`}>25</div>
									<div className={`weightButton ${ workoutSet["has35"] ? "active" : "" }`}>35</div>
									<div className={`weightButton ${ workoutSet["has45"] ? "active" : "" }`}>45</div>
								</div>
								: ""
								}
								{
								workoutSet.exercise.hasWeight ?
								<div className="horizontalContainer">
									<div className="subHeader">{ workoutSet.exercise.maxWeight } lbs</div>
								</div>
								: ""
								}
							</div>

							<div className="graphContainer">
								<svg viewBox="0 0 350 200" className="workoutGraph">
									<defs>
										<linearGradient spreadMethod="pad" id="oneRepMaxGradient" x1="0%" y1="100%" x2="0%" y2="0%">
											<stop offset="0%" style={{stopColor: "rgb(251, 216, 223)", stopOpacity: 1}} />
											<stop offset="100%" style={{stopColor: "rgb(255, 255, 255)", stopOpacity: 1}} />
										</linearGradient>
									</defs>

									<path className="stroke" d={ workoutSet.graph.path } />
									<path className="fill" d={ workoutSet.graph.pathFill } fill="url(#oneRepMaxGradient)" />
								</svg>
							</div>

							<div className="actionContainer">
								<div className="setActionButton">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
										<path d="M9 16.2L4.8 12l-1.4 1.4L9 19 21 7l-1.4-1.4L9 16.2z"/>
									</svg>
								</div>

								<div className="setActionButton">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
										<path d="M8 5v14l11-7z"/>
									</svg>
								</div>

								<div className="setActionButton">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
										<path d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z"/>
									</svg>
								</div>
							</div>

						</div>
					))
					}
					</div>
				</div>
			}
			</div>
		);
	}
}

export default WorkoutActivity;
