import React from "react";
import "./workout.css";

// Design: https://dribbble.com/shots/5715392-Adasse-Beta-2-Mobile-App-Design/attachments/1446974?mode=media
// Card palette: https://i.pinimg.com/originals/33/e1/da/33e1da639d67589891ddbfef0461cb04.png

class Workout extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			isEditMode: false,
			workouts: [],
			exercises: []
		};
	}

	selectWorkout = workoutIndex => event => {
		this.setState(({workouts}) => ({
			workouts: workouts.map((workout, itemIndex) => ({
				...workout,
				isHidden: itemIndex == workoutIndex ? false : true
			})),
			isEditMode: true
		}));
	}

	componentDidMount() {
		fetch("http://huntingtonbeach.beynum.com:9005/workout/api/workoutload", { credentials: "include" })
			.then(response => response.json())
			.then(data => {
				this.setState({
					exercises: data.exercises,
					workouts: data.workouts,
					isLoading: false
				});
			})
			.catch(error => {
				console.warn(error);
				this.props.toast("Error loading workouts", true);
			});
	}

	render() {
		return (
			<div className="page workoutPage">
				<h1>My Workouts</h1>

				{
				this.state.isLoading ?
					<div className="loading">Loading ...</div>
				:
					<div className="workoutCards">
					{
						this.state.workouts.map((workout, workoutIndex) => (
						<div key={workoutIndex} className={`workoutCard ${ workout.isHidden ? "hidden" : "" }`}>
							<div className="cardContent">
								<div className="cardDetails">
									<div className="cardHeader">{workout.name}</div>

									<div className="cardSubHeader">
										<div className="label">Last Workout: </div>
										<div className="data">{workout.lastWorkout ? workout.lastWorkout.toDateString() : "Not Started"}</div>
									</div>
									<div className="cardSubHeader">
										<div className="label">Sets</div>
										<div className="data">{workout.sets.length}</div>
										<div className="label">&nbsp;&bull;&nbsp;Reps</div>
										<div className="data">{workout.sets.reduce((total, current) => total + current.goalReps, 0)}</div>
									</div>
								</div>

								<div className="cardAction">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" onClick={this.selectWorkout(workoutIndex)}>
										<path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z"/>
									</svg>
								</div>
							</div>

							<div className="timelineContainer">
								<div className="timelineDate">
									<div className="timelineCount">33</div>
									<div className="timelineDay">Sun</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">&bull;</div>
									<div className="timelineDay">Mon</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">3</div>
									<div className="timelineDay">Tue</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">&bull;</div>
									<div className="timelineDay">Wed</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">10</div>
									<div className="timelineDay">Thu</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">1</div>
									<div className="timelineDay">Fri</div>
								</div>
								<div className="timelineDate">
									<div className="timelineCount">&bull;</div>
									<div className="timelineDay">Sat</div>
								</div>
							</div>
						</div>
						))

					}

						<div className={`workoutCard ${ this.state.isEditMode ? "hidden" : "" }`}>
							<div className="addWorkout">+</div>
						</div>

					</div>
				}
			</div>
		);
	}
}

export default Workout;
