/* global fetch */

import React from "react";
import "./home.css";
import loadingImage from "../media/loading.gif";

class Home extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			workouts: [],
			user: props.user,
			isLoading: true
		};
	}

	componentDidMount() {
		fetch("http://workout.beynum.com/workout/api/homeload", { credentials: "include" })
			.then(response => response.json())
			.then(data => {

				this.context.weeks = (new Array(4))
					.fill({ })
					.map((item, index) => {
						let week = new Date();
						week.setHours(0,0,0,0);
						week.setDate(week.getDate() - week.getDay() - (index * 7));

						return week;
					});

				this.setState({
					isLoading: false,
					workouts: data.workouts
				});

			});
	}

	render() {
		return (
			<div className="page">
				<h1>Welcome { this.state.user.firstName }</h1>

				{
				this.state.isLoading ?
					<div className="loading">
						<img alt="Loading" src={loadingImage} />
						<div>Loading...</div>
					</div>
				:
					<div className="workoutsContainer">

					{
					this.state.workouts.map((workout, workoutIndex) => (

						<div key={workoutIndex} className="workoutContainer">
							<div className="headerContainer">
								<div className="itemBar"></div>

								<div className="dataContainer">
									<div className="workoutHeader">{ workout.name }</div>
									<div className="workoutSubHeader">Sets: { workout.setCount }</div>
								</div>

								<div className="actionButton">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24px" height="24px">
										<path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
									</svg>
								</div>

								<div className="actionButton">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24px" height="24px">
										<path d="M8 5v14l11-7z"/>
									</svg>
								</div>
							</div>

							<div className={`detailContainer ${ workout.isVisible ? "active" : "" }`}>

								<table className="scheduleTable">
								<tbody>
								<tr>
									<th></th>
									<th>S</th>
									<th>M</th>
									<th>T</th>
									<th>W</th>
									<th>T</th>
									<th>F</th>
									<th>S</th>
								</tr>
								</tbody>
								</table>

							</div>
						</div>

					))
					}

					</div>
				}
			</div>
		);
	}
}

export default Home;
