import React from "react";
import "./activity.css"
import loadingImage from "../media/loading.gif";

class Activity extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			workout: { id: props.workoutId }
		};
	}

	getWeights = (totalWeight, exercise) => {
		let weights = {};

		totalWeight -= exercise.hasBar ? 45 : 0;

		weights.has45 = totalWeight >= 45 * 2;
		totalWeight -= totalWeight >= 45 * 2 ? 45 * 2 : 0;

		weights.has25 = totalWeight >= 25 * 2;
		totalWeight -= totalWeight >= 25 * 2 ? 25 * 2 : 0;

		weights.has10 = totalWeight >= 10 * 2;
		totalWeight -= totalWeight >= 10 * 2 ? 10 * 2 : 0;

		weights.has5 = totalWeight >= 5 * 2;
		totalWeight -= totalWeight >= 5 * 2 ? 5 * 2 : 0;

		weights["has2.5"] = totalWeight >= 2.5 * 2;
		totalWeight -= totalWeight >= 2.5 * 2 ? 2.5 * 2 : 0;

		return weights;
	}

	componentDidMount() {
		fetch("http://huntingtonbeach.beynum.com:9005/workout/api/activityload?id=" + this.state.workout.id, { credentials: "include" })
			.then(response => response.json())
			.then(data => {
				let workout = data.workout;
				workout.sets = data.workout.sets.map(setItem => ({
					...setItem,
					...this.getWeights(setItem.goalWeight, setItem.exercise)
				}));

				this.setState({
					workout: workout,
					isLoading: false
				});
			})
			.catch(error => {
				console.warn(error);
				this.props.toast("Error loading workout", true);
			});
	}

	componentWillUnmount() {
		if (this.state.interval) {
			clearInterval(this.state.interval);
		}
	}

	startActivity = () => {
		this.setState(() => ({
			activity: {
				workoutId: this.state.workout.id,
				runDate: new Date(),
				sets: [],
				minElapsed: 0,
				secElapsed: 0
			}
		}),
		() => {
			const interval = setInterval(this.updateTime, 1000);
			this.setState({ interval: interval });
		}
		);
	}

	updateTime = () => {
		this.setState(({activity}) => ({
			activity: {
				...activity,
				minElapsed: Math.floor((Date.now() - this.state.activity.runDate) / 1000 / 60),
				secElapsed: Math.floor(((Date.now() - this.state.activity.runDate) / 1000) % 60)
			}
		}));
	}

	render() {
		return (
			<div className="page activityPage">
			{
			this.state.isLoading ?
				<div className="loading">
					<img alt="Loading" src={loadingImage} />
					<div>Loading...</div>
				</div>
			:
				<div>
					<div className="activityCard">
						<div className="cardContent">
							<div className="cardHeader">{this.state.workout.name}</div>

							<div className="cardInfo">
								<div>time</div>
								<div className="cardData">
									{
										this.state.activity ? this.state.activity.minElapsed + " min " + this.state.activity.secElapsed + " sec"
										: "Not Started"
									}
								</div>
							</div>

							<div className="cardInfo">
								<div>sets remain</div>
								<div className="cardData">
									{
										this.state.activity ? this.state.workout.sets.length - this.state.activity.sets.length
										: "Not Started"
									}
								</div>
							</div>
						</div>

						<div className="cardRemaining">
							{ this.state.activity ?
							<svg viewBox="0 0 42 42">
								<circle className="setsSegment" cx="21" cy="21" r="15.91549430918954" stroke="#ce4b99" strokeWidth="3" strokeDasharray="85 15" strokeDashoffset="25"></circle>
								<text x="21" y="26" textAnchor="middle">{this.state.activity.sets.length > 0 ? Math.round((this.state.activity.sets.length / this.state.workout.sets.length) * 100) : 0 }</text>
							</svg>
							:
							<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" onClick={this.startActivity}>
								<path d="M8 5v14l11-7z"/>
							</svg>
							}
						</div>
					</div>

					{
					this.state.workout.sets.map((setItem, setIndex) => (
					<div key={setIndex} >
						<div className="setContainer">
							<div className="setTimeline">
								<div className={`timelineLine ${ setIndex === 0 ? "hidden" : "" }`}></div>
								<div className="setNumber">{setIndex + 1}</div>
								<div className="timelineLine"></div>
							</div>

							{
							//setItem.isActive ?
							setIndex === 2 ?
							<div className="setCard active">
								<div className="setCardContainer">
									<div className="setName">{ setItem.exercise.name }</div>

									<div className="repsContainer">
										<div className="reps">
											<div className="editButton">-</div>
											<div className="repsNumber">{setItem.goalReps}</div>
											<div className="editButton">+</div>
										</div>
										<div>Reps</div>
									</div>
								</div>

								<div className="setAction">
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
										<path d="M8 5v14l11-7z"/>
									</svg>
								</div>
							</div>
							:
							<div className="setCard">
								<div className="setCardContainer">
									<div className="setName">{ setItem.exercise.name }</div>

									<div className="cardInfo">
										<div>reps</div>
										<div className="cardData">
											{ 
											this.state.activity && this.state.activity.sets.length < setIndex ? this.state.activity.sets[setIndex].reps : setItem.goalReps
											}
										</div>
									</div>

									<div className={`cardInfo ${ setItem.exercise && setItem.exercise.hasWeight ? "" : "hidden" }`}>
										<div>weight</div>
										<div className="cardData">
										{ 
											this.state.activity && this.state.activity.sets.length < setIndex ? this.state.activity.sets[setIndex].weight : setItem.goalWeight
										} lbs
										</div>

										<div className={`weight ${ setItem.has45 ? "active" : "" } `}>45</div>
