import React from "react";
import Login from "./components/Login";
import Nav from "./components/Nav";
import Main from "./components/Main";
import Workout from "./components/Workout";
import Activity from "./components/Activity";
import Admin from "./components/Admin";
import Toast from "./components/Toast";

class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			isLoggedIn: false,
			toast: {
				text: "",
				isActive: false
			}
		};
	}

	showToast = message => {
		this.setState({
			toast: {
				text: message,
				isActive: true
			}
		});

		setTimeout(() => {
			this.setState({
				toast: {
					text: "",
					isActive: false
				}
			});
		}, 1000);
	}

	componentDidMount() {
		fetch("http://huntingtonbeach.beynum.com:9005/workout/api/appload")
			.then(response => response.json())
			.then(data => {

				this.setState({
					isLoggedIn: data.isLoggedIn,
					page: data.isLoggedIn ? <Main /> : <Login toast={this.showToast} />,
					isLoading: false
				});

			});
	}

	changePage = (page) => {
		if (this.state.user.id) {
			switch (page) {
				case "home":
					this.setState({ page: <Main /> });
					break;

				case "workout":
					this.setState({ page: <Workout /> });
					break;

				case "activity":
					this.setState({ page: <Activity /> });
					break;

				case "admin":
					this.setState({ page: <Admin /> });
					break;

				default:
					break;
			}
		}
	}

	render() {
		return (
			<div className="pageContainer">
			{
				this.state.isLoading ?
					<div>Loading</div>
				:
				this.state.page
			}
			{	
				this.state.isLoggedIn ? <Nav changePage={ this.changePage } /> : null
			}
				<Toast message={ this.state.toast } />
			</div>
		);
	}

}

export default App;
