import React from "react";
import "./workout.css";

class Workout extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			workouts: [
				{ id: 1, name: "Workout 1", lastWorkout: new Date(2020, 10, 10), style: {}, sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, goalWeight: 150, rest: 180 }, { exerciseId: 2, exerciseName: "Bench Press", goalReps: 10, rest: 145 }, { exerciseId: 3, exerciseName: "Row", goalReps: 10, rest: 145 }] },
				// { id: 2, name: "Workout 2", sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 3, name: "Workout 3", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 4, name: "Workout 4", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 5, name: "Workout 5", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 6, name: "Workout 6", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 7, name: "Workout 7", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 8, name: "Workout 8", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 9, name: "Workout 9", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] },
				// { id: 10, name: "Workout 10", lastWorkout: new Date(2020, 10, 10), sets: [{ exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }, { exerciseId: 1, exerciseName: "Hanging Knee Raise", goalReps: 15, rest: 180 }] }
				]
		};
	}

	setStyle = workout => {
		// console.dir(e);
		console.log(workout);
	}

	render() {
		return (
			<div className="page workoutPage">
				<div className="headerBackground">
					Workouts
				</div>

				{
				this.state.workouts.map(workout => (
				<div key={workout.id} className="card" style={workout.style}>
					<div className="cardContent" onClick={ this.setStyle(workout) }>
						<div className="cardHeader">{ workout.name }</div>
						<div className="cardSubHeader">
							{ workout.lastWorkout ? "Last " + workout.lastWorkout.toDateString() : "Not Started" }&nbsp;&bull;&nbsp;
							{ workout.sets.reduce((total, current) => total + current.goalReps, 0) + " total reps" }
						</div>
					</div>

					<div className="cardSelector">
						{
						workout.sets.map((setItem, setIndex) => (
							<div key={setIndex} className="subCard">
								<div className="subCardContent">
									<div className="setNumber">Set { setIndex + 1 }</div>
									<div className="exercise">{ setItem.exerciseName }</div>

									<div className="repWeightContainer">
										<div className="reps">
											<input type="number" value={setItem.goalReps} onChange={e => { }} placeholder="Reps" />
										</div>
										x
										<div className="weight">
											<input type="number" value={setItem.goalReps} onChange={e => {}} placeholder="Weight" />
										</div>
									</div>
								</div>
							</div>
						))
						}
					</div>
				</div>
				))
				}

				<div className="floatingButton">+</div>
			</div>
		);
	}
}

export default Workout;
